      out.write("\t\t\timg.style.filter = \"progid:DXImageTransform.Microsoft.AlphaImageLoader(src='\"+src+\"', sizingMethod='scale')\";\r\n");
      out.write("\t\t} catch (e) {\r\n");
      out.write("\t\t\tif (!document.getElementById(\"ie_preview_error_\" + View_area)) {\r\n");
      out.write("\t\t\t\tvar info = document.createElement(\"<p>\");\r\n");
      out.write("\t\t\t\tinfo.id = \"ie_preview_error_\" + View_area;\r\n");
