      out.write("\t\t\timg.style.width = '100px'; \r\n");
      out.write("\t\t\timg.style.height = '100px';\r\n");
      out.write("\t\t\tpreview.appendChild(img);\r\n");
      out.write("\t\t\tif (window.FileReader) { // FireFox, Chrome, Opera 확인.\r\n");
